/*


-* Count and Say *-



The count-and-say sequence is a sequence of digit strings defined by the recursive formula:

countAndSay(1) = "1"
countAndSay(n) is the way you would "say" the digit string from countAndSay(n-1), which is then converted into a different digit string.
To determine how you "say" a digit string, split it into the minimal number of substrings such that each substring contains exactly one unique digit. Then for each substring, say the number of digits, then say the digit. Finally, concatenate every said digit.

For example, the saying and conversion for digit string "3322251":


Given a positive integer n, return the nth term of the count-and-say sequence.



Example 1:

Input: n = 1
Output: "1"
Explanation: This is the base case.
Example 2:

Input: n = 4
Output: "1211"
Explanation:
countAndSay(1) = "1"
countAndSay(2) = say "1" = one 1 = "11"
countAndSay(3) = say "11" = two 1's = "21"
countAndSay(4) = say "21" = one 2 + one 1 = "12" + "11" = "1211"


Constraints:

1 <= n <= 30

*/

class A {
  String countAndSay(int n) {
    // base case
    if (n == 1) return "1";
    if (n == 2) return "11";

    // take a string equals 11
    String str = "11";

    // now we need the value of nth term so we loop from 3 -> n
    for (int i = 3; i <= n; i++) {
      // temp will have the ans of the next iteration i.e value of the next ith data
      String temp = "";
      str = str + "&"; // add a delimiter at the end
      int cnt = 1; // counter

      // now loop from 1st value to last value
      for (int j = 1; j < str.length; j++) {
        // this condition should be satisfied, if not that means new number has started occurring
        if (str[j] != str[j - 1]) {
          temp = temp + cnt.toString(); // add the counter to temp
          temp = temp + str[j - 1]; // add the data of the counter
          cnt = 1; // reset the counter to 1
        } else
          cnt++; // just count the freq of that number
      }

      // after one complete traversal, make str equal to temp;
      str = temp;
    }

    return str;
  }
}

class B {
// Runtime: 311 ms, faster than 100.00% of Dart online submissions for Count and Say.
// Memory Usage: 157.7 MB, less than 50.00% of Dart online submissions for Count and Say.
  String countAndSay(int n) {
    List<String> dp = List.filled(n + 1, '');
    dp[0] = "";
    for (int i = 1; i <= n; i++) {
      if (i == 1)
        dp[i] = "1";
      else if (i == 2)
        dp[i] = "11";
      else {
        String s = dp[i - 1];
        //cout<<s<<" ";
        String ch = s[0];
        int freq = 1;
        String str = "";
        for (int j = 1; j < s.length; j++) {
          if (s[j] != ch) {
            str += freq.toString();
            str += ch;
            ch = s[j];
            freq = 1;
          } else {
            freq++;
          }
        }
        str += freq.toString();
        str += ch;
        //cout<<str<<" ";
        dp[i] = str;
      }
    }
    return dp[n];
  }
}

class C {
  String convert(String s) {
    String previousCharacter = s[0];
    int previousCount = 1; //stores count of consecutive same digits
    StringBuffer ans = StringBuffer();
    for (int i = 1; i < s.length; i++) {
      String currentCharacter = s[i];
      if (currentCharacter != previousCharacter) {
        ans.write(previousCount);
        ans.write(previousCharacter);
        previousCount = 1; //reset count
        previousCharacter = currentCharacter;
      } else
        previousCount++;
    }
    ans.write(previousCount);
    ans.write(previousCharacter);
    return ans.toString();
  }

  String countAndSay(int n) {
    String previousString = "1";
    for (int i = 2; i <= n; i++) {
      String cnsNum = convert(previousString); //cns  = "count and say Num"
      previousString = cnsNum; //remember (dp) the previous built string
    }
    return previousString;
  }
}

class D {
  // Two Pointers - retuning charcode
  String countAndSay(int n) {
    String s = "1";
    for (int i = 2; i <= n; i++) {
      StringBuffer sb = StringBuffer();
      List<String> prev = s.split("");
      int left = 0, right = 0;
      //use something like two pointers to count substring char frequency
      while (right < prev.length) {
        while (right < prev.length && prev[left] == prev[right]) {
          right++;
        }
        sb.write("" + (right - left).toString() + prev[left]);
        left = right;
      }
      s = sb.toString();
    }
    return s;
  }
}

