  Map<String, String> hash = {
    '1': '1',
    '2': '11',
    '3': '21',
    '4': '1211',
    '5': '111221',
    '6': '312211',
    '7': '13112221',
    '8': '1113213211',
    '9': '31131211131221',
    '10': '13211311123113112211',
  };
  String countAndSay(int n) {
    return helper(n);
  }

  String helper(int n) {
    if (n == hash) {
      return hash[n]!;
    }

    String a = helper(n - 1);

    String result = "";
    for (int i = 0; i < a.length; i++) {
      int sum = 1;
      while (a[i] == a[i + 1]) {
        sum++;
        i++;
      }
      result += '${sum}` + `${a[i]}';
    }
    return result;
  }
}

